datadef.source:
(select distinct z.*,srl.zone,srr.NAME from (select y.SCENARIORUNID,y.SCENARIORUNRULEID,y.DTCATEGORYID,y.ZONEID,'' tierfamily from (select x.SCENARIORUNID,x.SCENARIORUNRULEID,x.DTCATEGORYID,x.ZONEID from (select srr.SCENARIORUNID,srr.SCENARIORUNRULEID,srp.DTCATEGORYID,srl.ZONEID,COALESCE(srp.TIERFAMILY, '') from dtopt.scenariorunproduct srp inner join dtopt.scenariorunproductlocation srpl on srp.SCENARIORUNID=srpl.SCENARIORUNID and srp.PRODUCTID=srpl.PRODUCTID inner join dtopt.scenariorunlocation srl on srpl.SCENARIORUNID=srl.SCENARIORUNID and srpl.LOCATIONID=srl.LOCATIONID inner join dtopt.scenariorunrule srr on srr.SCENARIORUNID=srp.SCENARIORUNID and srr.RULETYPE=33 and srr.ISENABLED=1 inner join dtopt.scenariorunproductgroupdetail srpgd on srpgd.SCENARIORUNID=srr.SCENARIORUNID and srr.PRODUCTGROUPID=srpgd.PRODUCTGROUPID and srpgd.PRODUCTID=srp.PRODUCTID group by srr.SCENARIORUNID,srr.SCENARIORUNRULEID,srp.DTCATEGORYID,srl.ZONEID,COALESCE(srp.TIERFAMILY, '')) x group by x.SCENARIORUNID,x.SCENARIORUNRULEID,x.DTCATEGORYID,x.ZONEID having count(*)=1) y where exists(select 1 from dtopt.scenariorunproduct srp2 where srp2.SCENARIORUNID=y.scenariorunid and srp2.DTCATEGORYID=y.dtcategoryid and COALESCE(srp2.TIERFAMILY, '') = '')
union select y.SCENARIORUNID,y.SCENARIORUNRULEID,y.DTCATEGORYID,y.ZONEID,y.tierfamily from (select x.SCENARIORUNID,x.SCENARIORUNRULEID,x.DTCATEGORYID,x.ZONEID,x.tierfamily from (select srr.SCENARIORUNID,srr.SCENARIORUNRULEID,srp.DTCATEGORYID,srl.ZONEID,COALESCE(srp.TIERFAMILY, '') tierfamily,srp.TIERCLASS from dtopt.scenariorunproduct srp inner join dtopt.scenariorunproductlocation srpl on srp.SCENARIORUNID=srpl.SCENARIORUNID and srp.PRODUCTID=srpl.PRODUCTID inner join dtopt.scenariorunlocation srl on srpl.SCENARIORUNID=srl.SCENARIORUNID and srpl.LOCATIONID=srl.LOCATIONID inner join dtopt.scenariorunrule srr on srr.SCENARIORUNID=srp.SCENARIORUNID and srr.RULETYPE=33 and srr.ISENABLED=1 inner join dtopt.scenariorunproductgroupdetail srpgd on srpgd.SCENARIORUNID=srr.SCENARIORUNID and srr.PRODUCTGROUPID=srpgd.PRODUCTGROUPID and srpgd.PRODUCTID=srp.PRODUCTID where COALESCE(srp.TIERFAMILY, '')!='' group by srr.SCENARIORUNID,srr.SCENARIORUNRULEID,srp.DTCATEGORYID,srl.ZONEID,COALESCE(srp.TIERFAMILY, ''),srp.TIERCLASS) x group by x.SCENARIORUNID,x.SCENARIORUNRULEID,x.DTCATEGORYID,x.ZONEID,x.tierfamily having count(*)<2) y
) z inner join dtopt.scenariorunlocation srl on z.zoneid=srl.ZONEID and z.scenariorunid=srl.SCENARIORUNID inner join dtopt.scenariorunrule srr on z.scenariorunruleid = srr.SCENARIORUNRULEID ) p
